      private void puntosKeyTyped(java.awt.event.KeyEvent evt) {                               

        char car = evt.getKeyChar();
        if( puntos.getText().length()>=2 ) evt.consume();
        if(( car<'0' || car>'9' )) evt.consume();
      }


