        // TODO add your handling code here:
        int puntoss = Integer.parseInt(puntos.getText());
     try
     {
            String conexion = "consult('parametros.pl')";
            Query con = new Query(conexion);
            System.out.println(conexion + "" + (con.hasSolution()?"ACEPTADO":"FALLADO"));
            System.out.println("INICIANDO DIAGNOSTICO");
            
             String consul = "main("+this.puntos.getText()+",X,Y,Z,A).";
            Query ejecutar = new Query(consul);
            if (ejecutar.hasSolution()){
                System.out.println("TU DIAGNOSTICO."+ejecutar.oneSolution().get("Z").toString());
                diagnostico.setText(String.valueOf(ejecutar.oneSolution().get("X").toString()));
                test.setText(String.valueOf(ejecutar.oneSolution().get("Y")+ejecutar.oneSolution().get("Z").toString()));
                INFORMACION.setText(String.valueOf(ejecutar.oneSolution().get("A").toString()));
                 switch(diagnostico.getText()){
                 }
            }
          
     } catch (Exception e) {
         e.printStackTrace();
          
     }
